package ex00;import java.io.FileInputStream;import java.io.FileNotFoundException;import java.util.*;public class Signature {    final static String signaturesPath = "src/ex00/signatures.txt";    public static Map<String, String> fileReader() {        Map<String, String> signatures = new HashMap<>();        try (Scanner in = new Scanner(new FileInputStream(signaturesPath))) {            while (in.hasNextLine()) {                String line = in.nextLine();                String[] lineArray = line.split(",");                signatures.put(lineArray[0], lineArray[1].replace(" ", ""));            }        } catch (FileNotFoundException e) {            e.printStackTrace();        }        return signatures;    }}