package edu.school21.printer.logic;public class Painter {    public static void printMass(byte[][] mass, char a, char b) {        if (mass == null) {            System.out.println("Произошла ошибка!");            return;        }        for (int i = 0; i < mass.length; ++i) {            for (int j = 0; j < mass[i].length; ++j) {                if (mass[i][j] == 0) System.out.print(a);                else System.out.print(b);            }            System.out.println();        }    }}