package ex01;import java.util.Scanner;public class Program {    public static void main(String[] args) {        int number = 0, i = 2;        Scanner in = new Scanner(System.in);        if (in.hasNextInt()) {            number = in.nextInt();            in.close();        } else {            in.close();            exitProg();        }        if (number <= 1) exitProg();        if (number == 2 || number == 3) {            System.out.println("true 1");            System.exit(1);        }        boolean optFind = false;        for (; i * i <= number; ++i) {            if (number % i == 0) {                System.out.println("false " + --i);                optFind = true;                break;            }        }        if (!optFind) {            System.out.println("true " + --i);        }    }    private static void exitProg() {        System.err.println("Illegal Argument");        System.exit(-1);    }}