package ex02;import java.util.Scanner;public class Program {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int tmp, count = 0;        while (sc.hasNext()) {            tmp = sc.nextInt();            if (tmp != 42) {                if (isSimple(sumOfDigits(tmp))) {                    ++count;                }            } else {                sc.close();                System.out.println("Count of coffee-request â€“ " + count);                System.exit(0);            }        }    }    private static int sumOfDigits(int num) {        int res = 0;        while (num > 0) {            res += num % 10;            num = num / 10;        }        return res;    }    public static boolean isSimple(int num) {        if (num == 2 || num == 3) return true;        for (int i = 2; i * i < num; ++i) {            if (num % i == 0) {                return false;            }        }        return true;    }}