package ex03;import java.util.Scanner;//Однако порядок ввода еженедельных данных не гарантируется, поэтому//данные недели 1 можно вводить после данных недели 2. Если порядок ввода//данных неправильный, выводится сообщение IllegalArgument и программа закрывается с кодом -1.public class Program {    public static void main(String[] args) {        Scanner in = new Scanner(System.in);        int tmp = 9, inInt, count = 1;        long res = 0;        while (in.hasNext() && count <= 18) {            String str = in.nextLine();            if (str.equals("Week " + count)) {                for (int i = 0; i < 5; ++i) {                    inInt = in.nextInt();                    if (inInt < tmp) {                        tmp = inInt;                    }                }                res = res * 10 + tmp;                tmp = 9;                ++count;            } else if (str.equals("42")) {                in.close();                printRes(res, count - 1);                System.exit(0);            } else {                in.close();                System.err.println("Illegal Argument");                System.exit(-1);            }            in.nextLine();        }    }    private static void printRes(long res, int countWeeks) {        int tmp = 0;        for (int i = 0; i < countWeeks; ++i) {            tmp *= 10;            tmp += (int) (res % 10);            res /= 10;        }        for (int i = 0; i < countWeeks; ++i) {            System.out.print("Week " + (i + 1) + " ");            int min = tmp % 10;            tmp /= 10;            for (int j = 0; j < min; ++j) {                System.out.print("=");            }            System.out.print(">\n");        }    }}