package ex04;import java.util.Scanner;public class Program {    public static void main(String[] args) {        Scanner in = new Scanner(System.in);        char[] counts = new char[65536];        char[] line = in.nextLine().toCharArray();        for (char c : line) {            ++counts[c];        }        int maxNum = 0, idRes = 0, countSym = 0;        char maxSym = ' ';        int[] resNum = new int[10];        char[] resSym = new char[10];        for (int j = 0; j < 10; ++j) {            for (int i = 0; i < 65535; ++i) {                if (maxNum < counts[i]) {                    maxNum = counts[i];                    maxSym = (char) i;                    idRes = i;                }            }            resNum[j] = maxNum;            resSym[j] = maxSym;            counts[idRes] = 0;            if (maxNum != 0) {                countSym++;            }            maxNum = 0;        }        in.close();        printRes(resNum, resSym, countSym);    }    private static void printRes(int[] nums, char[] sym, int countSym) {        int maxValue = nums[0];        double divValue = (double) maxValue / 10;        for (int i = 11; i > 0; --i) {            for (int j = 0; j < countSym; ++j) {                if ((int) (nums[j] / divValue) + 1 == i) {                    System.out.print(nums[j] + "\t");                }                if (nums[j] / divValue >= i) {                    System.out.print("#\t");                }            }            System.out.println(" ");        }        for (int i = 0; i < countSym; ++i) {            System.out.print(sym[i] + "\t");        }    }}