package ex05;import java.util.Scanner;public class Program {    public static void main(String[] args) {        String tmp;        String[] students = new String[10];        boolean[][] dateTime = new boolean[30][5];        int[][][] visits = new int[30][5][10];        int countDots = 0, countstudents = 0;        Scanner in = new Scanner(System.in);        while (in.hasNext()) {            tmp = in.next();            if (tmp.equals(".")) {                ++countDots;            }            if (countDots == 0) {                students[countstudents] = tmp;                ++countstudents;            }            if (countDots == 1 && !tmp.equals(".")) {                int time = (int) tmp.toCharArray()[0] - 48;                int weekDay = switch (in.next()) {                    case "TU" -> 0;                    case "WE" -> 1;                    case "TH" -> 2;                    case "FR" -> 3;                    case "SA" -> 4;                    case "SU" -> 5;                    default -> 6;                };                for (int i = weekDay; i < 30; i = i + 7) {                    dateTime[i][time - 1] = true;                }            }            if (countDots == 2 && !tmp.equals(".")) {                int studentIndex = findStudent(students, tmp);                int time = in.nextInt();                int day = in.nextInt();                int checkStudent;                if (in.next().equals("HERE")) checkStudent = 1;                else checkStudent = -1;                visits[day - 1][time - 1][studentIndex] = checkStudent;            }            if (countDots == 3) {                printTable(students, dateTime, visits);                in.close();                System.exit(0);            }        }    }    private static int findStudent(String[] students, String name) {        for (int i = 0; students[i] != null; ++i) {            if (name.equals(students[i])) {                return i;            }        }        return -1;    }    private static void printTable(String[] students, boolean[][] dateTime, int[][][] visits) {        String day;        System.out.print("          ");        for (int i = 0; i < 30; ++i) {            for (int j = 0; j < 5; ++j) {                if (dateTime[i][j]) {                    day = switch (i % 7) {                        case 0 -> "TU";                        case 1 -> "WE";                        case 2 -> "TH";                        case 3 -> "FR";                        case 4 -> "SA";                        case 5 -> "SU";                        default -> "MO";                    };                    System.out.printf("%d:00 %s%3d|", j + 1, day, i + 1);                }            }        }        System.out.println(" ");        for (int i = 0; i < 10; ++i) {            if (students[i] != null) {                System.out.printf("%10s", students[i]);                for (int j = 0; j < 30; ++j) {                    for (int k = 0; k < 5; ++k) {                        if (visits[j][k][i] != 0) {                            System.out.printf("%10d|", visits[j][k][i]);                        } else if (dateTime[j][k]) {                            System.out.print("          |");                        }                    }                }                System.out.println(" ");            }        }    }}