package ex00;public class User {    private final int idUser;    private final String name;    private int balance;    User(int idUser, String name, int balance) {        this.idUser = idUser;        this.balance = 0;        setBalance(balance);        this.name = name;    }    public int getBalance() {        return balance;    }    public void setBalance(int balance) {        if (balance >= 0) this.balance = balance;    }    public String getName() {        return name;    }    public int getIdUser() {        return idUser;    }}