package ex02;public class Program {    public static void main(String[] args) {        User Mike = new User("Mike", 1000);        User John = new User("John", 1000);        User aboba = new User("aboba", 2000);        User Mike1 = new User("Mike", 1000);        User John1 = new User("John", 1000);        User aboba1 = new User("aboba", 2000);        User Mike2 = new User("Mike", 1300);        User John2 = new User("John", 1600);        User aboba2 = new User("aboba", 2000);        User Mike3 = new User("Mike", 1000);        User John3 = new User("John", 1000);        User aboba3 = new User("aboba", 2000);        UserArrayList data = new UserArrayList();        data.addUser(Mike);        data.addUser(John);        data.addUser(aboba);        data.addUser(Mike1);        data.addUser(John1);        data.addUser(aboba1);        data.addUser(Mike2);        data.addUser(John2);        data.addUser(aboba2);        data.addUser(Mike3);        data.addUser(John3);        data.addUser(aboba3);        printUsers(data);    }    private static void printUsers(UserArrayList users) {        for (int i = 0; i < users.getSize(); ++i) {            printUser(users.getUserByIndex(i));        }    }    private static void printUser(User user) {        System.out.println(user.getName() + ": " + user.getBalance() + ", id: " + user.getId());    }}