package ex02;public class UserArrayList implements UsersList {    private int size = 0;    private int capacity = 10;    private User[] users = new User[capacity];    public void addUser(User user) {        if (size == capacity) {            User[] tmp = users;            capacity *= 2;            users = new User[capacity];            System.arraycopy(tmp, 0, users, 0, capacity / 2);        }        users[size] = user;        ++size;    }    public User getUserByID(int id) throws UserNotFoundException {        for (int i = 0; i < size; ++i) {            if (users[i].getId() == id) return users[i];        }        throw new UserNotFoundException("Can't find user by ID");    }    public User getUserByIndex(int i) throws ArrayIndexOutOfBoundsException {        if (i < size) return users[i];        throw new ArrayIndexOutOfBoundsException("There is no such element");    }    public int getSize() {        return size;    }}