package ex00;import java.io.*;import java.util.*;public class Main {    static String fileName;    static Map<String, String> signatures;    public static void main(String[] args) {        signatures = Signature.fileReader();        try (Scanner in = new Scanner(System.in)) {            while (in.hasNext()) {                fileName = in.next();                if (!fileName.equals("42")) {                    checkFile();                } else return;            }        } catch (Exception e) {            e.getMessage();        }    }    private static void checkFile() {        StringBuilder result = new StringBuilder();        try (FileInputStream fileInputStream = new FileInputStream(fileName);             FileOutputStream fos = new FileOutputStream("src/ex00/result.txt", true)) {            byte[] bytes = fileInputStream.readNBytes(8);            for (byte b : bytes) {                result.append(String.format("%02X", b));            }            for (Map.Entry<String, String> pair : signatures.entrySet()) {                if (result.toString().contains(pair.getValue())) {                    fos.write(pair.getKey().getBytes());                    fos.write('\n');                    System.out.println("PROCESSED");                    return;                }            }            System.out.println("UNDEFINED");        } catch (IOException e) {            System.err.println("File not found");        }    }}