package ex02;class Counter implements Runnable {    private final int start;    private final int end;    private final int[] mass;    private int sum;    private final ShareSum shareSum;    Counter(int start, int end, int[] mass, ShareSum sum) {        this.start = start;        this.end = end;        this.mass = mass;        this.shareSum = sum;    }    @Override    public void run() {        for (int i = start; i < end; ++i) {            sum += mass[i];        }        System.out.println("Thread " + Thread.currentThread().threadId() + ": from " + start + " to " + (end - 1) + " sum is " + sum);        shareSum.setSum(sum);    }}