package edu.school21.printer.logic;import com.diogonunes.jcdp.color.ColoredPrinter;import com.diogonunes.jcdp.color.api.Ansi;import javax.imageio.ImageIO;import java.awt.image.BufferedImage;import java.io.File;import java.io.IOException;public class Image {    private static byte[][] imageInByte;    private static void printImage(String color1, String color2) {        ColoredPrinter printer = new ColoredPrinter();        for (int i = 0; i < 16; ++i) {            for (int j = 0; j < 16; ++j) {                if (imageInByte[i][j] != 0)                    printer.print(" ", Ansi.Attribute.NONE, Ansi.FColor.BLACK, Ansi.BColor.valueOf(color1));                else printer.print(" ", Ansi.Attribute.NONE, Ansi.FColor.BLACK, Ansi.BColor.valueOf(color2));            }            System.out.println();        }    }    private static void parseImage() {        File file = new File("src/resources/images/it.bmp");        try {            BufferedImage bufferedImage = ImageIO.read(file);            imageInByte = new byte[16][16];            for (int i = 0; i < imageInByte.length; ++i) {                for (int j = 0; j < imageInByte[i].length; ++j) {                    imageInByte[i][j] = (byte) bufferedImage.getRGB(j, i);                }            }        } catch (IOException e) {            e.getMessage();        }    }    public static void showImage(String color1, String color2) {        parseImage();        printImage(color1, color2);    }}