package ex00;public class Program {    public static void main(String[] args) {        User Mike = new User(1, "Mike", 1000);        User John = new User(2, "John", 1000);        printUser(Mike);        printUser(John);        Transaction transaction1 = new Transaction("John", "Mike", TransferCategory.OUTCOME, -500);        Transaction transaction2 = new Transaction("Mike", "John", TransferCategory.INCOME, 500);        printTransaction(transaction1);        printTransaction(transaction2);    }    private static void printUser(User user) {        System.out.println(user.getName() + ": " + user.getBalance());    }    public static void printTransaction(Transaction transaction) {        System.out.print(transaction.getSender() + " -> " + transaction.getRecipient());        if (transaction.getCategory() == TransferCategory.OUTCOME) System.out.print(", ");        else System.out.print(", +");        System.out.println(transaction.getAmount() + ", " + transaction.getCategory()                + ", transaction " + transaction.getIdTransaction());    }}