package ex02;import ex01.UserIdsGenerator;public class User {    private final int id;    private final String name;    private int balance;    User(String name, int balance) {        this.id = UserIdsGenerator.getInstance().generateId();        this.balance = 0;        setBalance(balance);        this.name = name;    }    public int getBalance() {        return balance;    }    public void setBalance(int balance) {        if (balance >= 0) this.balance = balance;    }    public String getName() {        return name;    }    public int getId() {        return id;    }}