package ex04;import java.util.UUID;enum TransferCategory {    OUTCOME,    INCOME}public class Transaction {    private UUID idTransaction;    private String recipient;    private String sender;    private TransferCategory category;    private int amount;    Transaction(String recipient, String sender, int amount, TransferCategory category) {        idTransaction = UUID.randomUUID();        this.recipient = recipient;        this.sender = sender;        this.category = category;        this.amount = 0;        setAmount(amount, category);    }    public Integer getAmount() {        return amount;    }    public void setAmount(int amount, TransferCategory category) {        if ((category == TransferCategory.INCOME && amount > 0) ||                (category == TransferCategory.OUTCOME && amount < 0))            this.amount = amount;    }    public String getSender() {        return sender;    }    public void setSender(String sender) {        this.sender = sender;    }    public String getRecipient() {        return recipient;    }    public void setRecipient(String recipient) {        this.recipient = recipient;    }    public TransferCategory getCategory() {        return category;    }    public void setCategory(TransferCategory category) {        this.category = category;    }    public UUID getIdTransaction() {        return idTransaction;    }    public void printTransferInfo() {        System.out.print("Sender: " + this.getSender() + ". Recipient: " + this.recipient);        System.out.println(". Category: " + this.category + ". Amount: " + this.amount);    }    public void setIdentifier(UUID idTransaction) {        this.idTransaction = idTransaction;    }}