package ex04;import ex01.UserIdsGenerator;public class User {    private final int id;    private final String name;    private int balance;    private TransactionsLinkedList transactionsList;    User(String name, int balance) {        this.id = UserIdsGenerator.getInstance().generateId();        this.balance = 0;        setBalance(balance);        this.name = name;        this.transactionsList = new TransactionsLinkedList();    }    public int getBalance() {        return balance;    }    public void setBalance(int balance) {        if (balance >= 0) this.balance = balance;    }    public String getName() {        return name;    }    public int getId() {        return id;    }    public TransactionsLinkedList getTransactionsList() {        return transactionsList;    }    @Override    public String toString() {        return "User name = " + name +                "\nidentifier = " + id +                "\nbalance = " + balance;    }}