package edu.school21.spring.service.config;import edu.school21.spring.service.repositories.UsersRepositoryJdbcImpl;import edu.school21.spring.service.repositories.UsersRepositoryJdbcTemplateImpl;import edu.school21.spring.service.services.UsersService;import edu.school21.spring.service.services.UsersServiceImpl;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.jdbc.datasource.embedded.EmbeddedDatabaseBuilder;import org.springframework.jdbc.datasource.embedded.EmbeddedDatabaseType;import javax.sql.DataSource;@Configurationpublic class TestApplicationConfig {    @Bean    DataSource dataSource() {        return new EmbeddedDatabaseBuilder()                .setType(EmbeddedDatabaseType.HSQL)                .build();    }    @Bean("usersServiceJdbcBean")    UsersService usersServiceJdbc(DataSource dataSource) {        return new UsersServiceImpl(new UsersRepositoryJdbcImpl(dataSource));    }    @Bean("usersServiceJdbcTemplateBean")    UsersService usersServiceJdbcTemplate(DataSource dataSource) {        return new UsersServiceImpl(new UsersRepositoryJdbcTemplateImpl(dataSource));    }}