package edu.school21.models;import java.util.Objects;public class User {    private Long identifier;    private String login;    private String password;    private boolean authStatus;    public User(Long identifier, String login, String password, boolean authStatus) {        this.identifier = identifier;        this.login = login;        this.password = password;        this.authStatus = authStatus;    }    public Long getIdentifier() {        return identifier;    }    public void setIdentifier(Long identifier) {        this.identifier = identifier;    }    public String getLogin() {        return login;    }    public void setLogin(String login) {        this.login = login;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public boolean isAuthStatus() {        return authStatus;    }    public void setAuthStatus(boolean authStatus) {        this.authStatus = authStatus;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof User user)) return false;        return authStatus == user.authStatus && Objects.equals(identifier, user.identifier) && Objects.equals(login, user.login) && Objects.equals(password, user.password);    }    @Override    public int hashCode() {        return Objects.hash(identifier, login, password, authStatus);    }    @Override    public String toString() {        return "User{" +                "identifier=" + identifier +                ", login='" + login + '\'' +                ", password='" + password + '\'' +                ", authStatus=" + authStatus +                '}';    }}