package edu.school21.services;import edu.school21.exceptions.AlreadyAuthenticatedException;import edu.school21.exceptions.EntityNotFoundException;import edu.school21.models.User;import edu.school21.services.UsersServiceImpl;import org.junit.jupiter.api.AfterEach;import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import org.springframework.jdbc.datasource.embedded.EmbeddedDatabase;import org.springframework.jdbc.datasource.embedded.EmbeddedDatabaseBuilder;import org.springframework.jdbc.datasource.embedded.EmbeddedDatabaseType;import static org.junit.jupiter.api.Assertions.*;public class UserServiceImplTest {    UsersServiceImpl usersService;    private EmbeddedDatabase dataSource;    private final String userNotFoundLogin = "ak";    private final String userNotFoundPassword = "";    @BeforeEach    public void init() {        dataSource = new EmbeddedDatabaseBuilder().setType(EmbeddedDatabaseType.HSQL)                .addScript("schema.sql").addScript("data.sql").build();        usersService = new UsersServiceImpl(dataSource);    }    @Test    public void testAuthenticate() {        assertThrows(EntityNotFoundException.class, () ->                usersService.authenticate(userNotFoundLogin, userNotFoundPassword));        assertThrows(AlreadyAuthenticatedException.class, () ->                usersService.authenticate("aboba", "111"));        assertFalse(usersService.authenticate("gleb", "23"));        assertTrue(usersService.authenticate("gleb", "111"));    }    @AfterEach    void close() {        dataSource.shutdown();    }}