CREATE SCHEMA IF NOT EXISTS shop;DROP TABLE IF EXISTS shop.product;DROP TABLE IF EXISTS shop.user;CREATE TABLE shop.product(    identifier INTEGER GENERATED BY DEFAULT AS IDENTITY (        START WITH 1 INCREMENT BY 1) PRIMARY KEY,    name       VARCHAR(50),    price      INTEGER);CREATE TABLE shop.user(    identifier  INTEGER GENERATED BY DEFAULT AS IDENTITY (        START WITH 1 INCREMENT BY 1) PRIMARY KEY,    login       VARCHAR(50),    password    VARCHAR(50),    auth_status BOOLEAN);