package ex01;public class Main {    static int numOfCount;    public static void main(String[] args) {        if (args.length != 1 || !args[0].startsWith("--count=")) {            System.err.println("Error!!");            System.exit(-1);        }        try {            numOfCount = Integer.parseInt(args[0].substring("--count=".length()));            if (numOfCount < 1) {                System.err.println("Error!! Value less 1");                System.exit(-1);            }            Object lock = new Object();            Thread egg = new Thread(new Egg(lock, numOfCount));            Thread hen = new Thread(new Hen(lock, numOfCount));            egg.start();            hen.start();        } catch (Exception e) {            System.out.println("Error((");        }    }}